import React, { useEffect, useState } from 'react';

export default function Home() {
  const [shows, setShows] = useState([]);
  const [expandedSummary, setExpandedSummary] = useState(null);

  const fetchdata = async () => {
    try {
      const res = await fetch('https://api.tvmaze.com/search/shows?q=all');
      const responseData = await res.json();
      setShows(responseData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchdata();
  }, []);

  const truncateSummary = (summary, maxLength) => {
    if (summary.length > maxLength) {
      return `${summary.substring(0, maxLength)}...`;
    }
    return summary;
  };

  const handleShowMore = (summary) => {
    setExpandedSummary(summary);
  };

  return (
    <>
      <h1>TV Shows</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {shows.map((show) => (
          <div key={show.show.id} style={{ border: '1px solid #ccc', borderRadius: '8px', margin: '10px', padding: '10px', width: '300px' }}>
            <h2>{show.show.name}</h2>
            <p>{truncateSummary(show.show.summary, 100)}</p>
            {show.show.summary.length > 100 && (
              <button onClick={() => handleShowMore(show.show.summary)}>Show more</button>
            )}
            {expandedSummary === show.show.summary && (
              <p>{show.show.summary}</p>
            )}
            <img
              src={show.show.image && show.show.image.medium}
              alt={show.show.name}
              style={{ width: '100%', borderRadius: '4px' }}
            />
          </div>
        ))}
      </div>
    </>
  );
}
