import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './details.css';

export default function Details() {
  const { showId } = useParams();
  const [showDetails, setShowDetails] = useState(null);
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    const fetchShowDetails = async () => {
      try {
        const res = await fetch(`https://api.tvmaze.com/shows/${showId}`);
        const responseData = await res.json();
        setShowDetails(responseData);

        if (responseData._links && responseData._links.previousepisode) {
          const prevEpisodeRes = await fetch(responseData._links.previousepisode.href);
          const prevEpisodeData = await prevEpisodeRes.json();
          setPreviousEpisode(prevEpisodeData);
        }
      } catch (error) {
        console.error('Error fetching show details:', error);
      }
    };

    fetchShowDetails();
  }, [showId]);

  const parseHtmlContent = (htmlString) => {
    const parser = new DOMParser();
    const doc = parser.parseFromString(htmlString, 'text/html');
    return doc.body.innerHTML;
  };

  const handlePopupToggle = () => {
    setShowPopup(!showPopup);
  };

  return (
    <div className="card-container">
      {showDetails ? (
        <div className="details-container">
          <div className="image-container">
            <img src={showDetails.image && showDetails.image.medium} alt={showDetails.name} />
            <div className='name'>
              <h1>{showDetails.name}</h1>
              <p style={{ color: 'white' }}>Language: {showDetails.language}</p>
              <p style={{ color: 'white' }}>Rating: {showDetails.rating && showDetails.rating.average}</p>
              <p style={{ color: 'white' }}>Genres: {showDetails.genres && showDetails.genres.join(', ')}</p>
              <button onClick={handlePopupToggle}>Show Previous Episode</button>
            </div>
          </div>

          <div dangerouslySetInnerHTML={{ __html: parseHtmlContent(showDetails.summary) }} />

          {showPopup && previousEpisode && (
            <div className="overlay">
              <div className="popup">
                <h2>Previous Episode Details</h2>
                <p>Title: {previousEpisode.name}</p>
                <p>Season: {previousEpisode.season}</p>
                <p>Episode: {previousEpisode.number}</p>
                <button onClick={handlePopupToggle}>Close</button>
              </div>
            </div>
          )}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
