import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

export default function Home() {
  const [shows, setShows] = useState([]);

  const fetchdata = async () => {
    try {
      const res = await fetch('https://api.tvmaze.com/search/shows?q=all');
      const responseData = await res.json();
      setShows(responseData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchdata();
  }, []);

  return (
    <>
      <h1>TV Shows</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap',flexDirection:'row' }}>
        {shows.map((show) => (
          <div key={show.show.id} style={{ border: '1px solid #ccc', borderRadius: '8px', margin: '10px', padding: '10px', width: '50px' }}>
            <h2>{show.show.name}</h2>
            
            <img
              src={show.show.image && show.show.image.medium}
              alt={show.show.name}
              style={{ width: '100%', borderRadius: '4px' }}
            />

            {/* Add a button to navigate to the details page */}
            <Link to={`/details/${show.show.id}`}>
              <button>View Details</button>
            </Link>
          </div>
        ))}
      </div>
    </>
  );
}
